import React, {useState} from 'react'
import { ethers } from 'ethers';
import SimpleStore_abi from './SimpleStore_abi.json';
import './App.css';

const SimpleStore = () => {

    const constactAdress = '0xd9145CCE52D386f254917e481eB44e9943F39138'
    const [ErrorMessage, setErrorMessage] = useState(null);
    const [defaultAccount, setDefaultAccount] = useState(null);
    const [connButtonText, setconnButtonText] = useState('Connect wallet');

    const [currentContraVal, setCurrentContraVal] = useState(null);
    const [provider, setProvider] = useState(null);
    const [signer, setSigner] = useState(null);
    const [Contract,setContract] = useState(null);

    const connectWalletHandler = () =>
    {
        if (window.ethereum)
        {
            window.ethereum.request({method: 'eth_requestAccounts'})
            .then(result => {
                accountChangeHandler(result[0]);
                setconnButtonText('wallet connected');
            })
        } else
        {
            setErrorMessage('Need to install Metamask');
        }
    }

    const accountChangeHandler = (newAccount) => {
        setDefaultAccount(newAccount);
        updateEthers();
    }

    const updateEthers = () =>
    {
        let tempProvider = new ethers.providers.Web3Provider(window.ethereum);
        setProvider(tempProvider);

        let tempSigner = tempProvider.getSigner();
        setSigner(tempSigner);
        let tempContract = new ethers.Contract(constactAdress, SimpleStore_abi, tempSigner);
        setContract(tempContract);

    }

    const getCurrentVal = async () =>
    {
        let val = await Contract.get();
        setCurrentContraVal(val);
    }

    const setHandler = (event) =>{
        event.preventDefault();
        Contract.set(event.target.setText.value)
    }
    return (
        <div>
            <h3>{"get/set Interaction with contract"}</h3>
            <button onClick={connectWalletHandler}> {connButtonText}</button>
            <h3 className="Adress-color">Address: {defaultAccount}</h3>
            <form onSubmit={setHandler}>
                <input id='setText' type='text'></input>
                <button type={"submit"}>Update</button>
            </form>
            <button onClick={getCurrentVal}>get current value</button>
            {currentContraVal}
            {ErrorMessage}
        </div>
    ) 
}

export default SimpleStore;
